package ex01_변수;

public class Ex04_변수추가 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 상수 - 절대로 값이 변하지 않는 변수
		
		final double pi = 3.141592;   // final 키워드를 만들어주면 상수가 됨 (값 수정x)
		
		
		// 문자열 저장 방법
		// 문자열 - 2글자 이상 해당되는 글자 >> char에 저장 불가
		String text = "안녕하세요?";     // 레퍼런스 자료형 (기본자료형 8개 빼고 모두 다 레퍼런스 자료형)
		System.out.println(text);    // 레퍼런스 자료형 특징 - 첫글자가 대문자 (기본자료형은 소문자로 시작함)
		
	
		
		

	}

}
